import React, { useEffect } from 'react'
import '../pages/index.scss'
import Link from 'next/link'
import { useDispatch, useSelector } from 'react-redux'
import { Button, Card, Navbar, Nav, NavDropdown, Form, FormControl } from 'react-bootstrap'
import { GET_POSTS_REQUEST } from '../reducers/post/postAction'
// import Card from '../Function/card'

const community = () => {
    const dispatch = useDispatch()
    const { post } = useSelector(state => state.post)

    useEffect(() => {
        dispatch({
            type: GET_POSTS_REQUEST
        })
    }, [])
    // <Link href="/question">
    //                 <a>
    //                     <Button variant="danger" size="lg">STOP</Button>
    //                 </a>
    //             </Link>

    return (
        <div className="container_">
            <div className="header">
                <Navbar bg="light" expand="lg">
                    <Link href="/index">
                        <a>
                            <Navbar.Brand href="#home">STOP,GOMEAN</Navbar.Brand>
                        </a>
                    </Link>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="mr-auto">
                            <Nav.Link href="#home">Home</Nav.Link>
                            <Nav.Link href="#link">Link</Nav.Link>
                            <NavDropdown title="Dropdown" id="basic-nav-dropdown">
                                <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                                <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
                                <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                                <NavDropdown.Divider />
                                <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
                            </NavDropdown>
                        </Nav>
                        <Form inline>
                            <FormControl type="text" placeholder="Search" className="mr-sm-2" />
                            <Button variant="outline-success">Search</Button>
                        </Form>
                    </Navbar.Collapse>
                </Navbar>
            </div>
            <div className="middle">

                {post && post.map(item => {
                    return (
                        <>
                            <Card style={{ width: '18rem', flexDirection: 'row', border: '1px solid black' }}>
                                <Card.Img variant="top" src="./images/image" />
                                <Card.Body>
                                    <Card.Title>{item.title}</Card.Title>
                                    <Card.Text>
                                        {item.tags && item.tags.map(v => {
                                            return (
                                                <p className="tagText">{'#' + v}</p>
                                            )
                                        })}
                                    </Card.Text>
                                    <Button variant="primary">more</Button>
                                </Card.Body>
                            </Card>
                        </>
                    )
                })}

            </div>
            <div className="bottom"></div>
        </div >
    )
}

export default community;
