import React from 'react'
import { useSelector } from 'react-redux'
import { Accordion, Card, Button } from 'react-bootstrap'
import Link from 'next/link'


const RecommendItem = () => {

    const { product } = useSelector(state => state.product)


    return (
        <>
            {product
                ?
                <Accordion defaultActiveKey="0">
                    {
                        product.map(item => {
                            return (
                                <Card >
                                    <Accordion.Toggle as={Card.Header} eventKey={item.name}>
                                        {item.name}
                                    </Accordion.Toggle>
                                    <Accordion.Collapse eventKey={item.name}>
                                        <Card.Body>{item.comment.map(value => {
                                            <Link key={value.id} href={`community/${value.id}`}><a><div>좋아요: {value.like}</div>{value.content}</a></Link>
                                        })}</Card.Body>
                                    </Accordion.Collapse>
                                </Card>
                            )
                        })
                    }
                </Accordion >
                : <div>404 not found</div>
            }
            <Accordion defaultActiveKey="0">
                <Card>
                    <Accordion.Toggle as={Card.Header} eventKey="0">
                        {product[0].name}
                    </Accordion.Toggle>

                </Card>
                <Card>
                    <Accordion.Toggle as={Card.Header} eventKey="1">
                        Click me!
    </Accordion.Toggle>
                    <Accordion.Collapse eventKey="1">
                        <Card.Body>Hello! I'm another body</Card.Body>
                    </Accordion.Collapse>
                </Card>
            </Accordion>
        </>
    )
}

export default RecommendItem
