import produce from 'immer'

import { GET_POSTS_FAILURE, GET_POSTS_REQUEST, GET_POSTS_SUCCESS, POST_POSTS_REQUEST, POST_POSTS_FAILURE, POST_POSTS_SUCCESS } from './postAction'


const initialState = {
    post: ["123123"]
}


const dummy = [
    {
        "name": "에어팟",
        "desc": "요즘 인싸아이템!! 지하철에서 에어팟을 안낀 사람이 없다!! 상대가 에이팟이 없다면!",
        "common_price": 200000,
        "comment": [],
        "weight": 0.85
    },
    {
        "name": "지갑",
        "desc": "설명",
        "common_price": 300000,
        "comment": [
            {
                "content": "남자면 몽블랑정도가 적당하지않을까요?",
                "likes": 2,
                "postID": 1
            }
        ],
        "weight": 0.8
    },
    {
        "name": "이모티콘",
        "desc": "선물주어야하지만 친하지않거나 가볍게 선물할때",
        "common_price": 2000,
        "comment": [],
        "weight": 0.78
    }

]

export default (state = initialState, action) => {
    return produce(state, draft => {
        switch (action.type) {

            // 게시글 전부 가져오기
            case GET_POSTS_REQUEST: {
                break;
            };
            case GET_POSTS_FAILURE: {
                break;
            }
            case GET_POSTS_SUCCESS: {
                console.log(dummy)
                draft.post = action.data
                break;
            }

            // 게시글 작성
            case POST_POSTS_REQUEST: {
                break;
            }
            case POST_POSTS_FAILURE: {
                break;
            }
            case POST_POSTS_SUCCESS: {
                break;
            }
            default: {
                return draft;
            }
        }
    })
}
